INSERT INTO S_ExecutionFiles VALUES(12,'queries.py',NULL,'cl_compute/optimization/queries.py','get_demand_sql = """select df.ItemId, df.LocationId, dp.PeriodStart, SUM(df.Quantity) as qty, 
